{"title":"Top Source IP by Packets ","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": {
    "url": {
      "%context%": "true",
      "%timefield%": "aws.networkfirewall.event.timestamp",
      "index": "flint_*networkfirewall*__mview",
      "body": {
        "size": 0,
        "aggs": {
          "source_ips": {
            "terms": {
              "field": "aws.networkfirewall.event.src_ip",
              "size": 10,
              "order": {
                "total_packets": "desc"
              }
            },
            "aggs": {
              "total_packets": {
                "sum": {
                  "field": "aws.networkfirewall.event.netflow.pkts"
                }
              },
              "time_series": {
                "date_histogram": {
                  "field": "aws.networkfirewall.event.timestamp",
                  "fixed_interval": "24h"
                },
                "aggs": {
                  "packet_count": {
                    "sum": {
                      "field": "aws.networkfirewall.event.netflow.pkts"
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "format": {"property": "aggregations.source_ips.buckets"}
  },
  "transform": [
    {
      "flatten": ["time_series.buckets"],
      "as": ["time_data"]
    },
    {
      "calculate": "datum.total_packets.value >= 1e9 ? format(datum.total_packets.value/1e9, '.2f') + ' GP' : datum.total_packets.value >= 1e6 ? format(datum.total_packets.value/1e6, '.2f') + ' MP' : datum.total_packets.value >= 1e3 ? format(datum.total_packets.value/1e3, '.2f') + ' KP' : datum.total_packets.value",
      "as": "formatted_packets"
    },
    {
      "calculate": "datum.key + ' (' + datum.formatted_packets + ')'",
      "as": "source_ip_with_total"
    }
  ],
  "mark": {
    "type": "area",
    "point": {
      "filled": false,
      "fill": "white"
    }
  },
  "encoding": {
    "x": {
      "field": "time_data.key",
      "type": "temporal",
      "timeUnit": "yearmonthdatehoursminutes",
      "axis": {
        "title": "Time",
        "format": "%Y-%m-%d",
        "labelAngle": -45
      }
    },
    "y": {
      "field": "time_data.packet_count.value",
      "type": "quantitative",
      "title": "Packets",
      "axis": {
        "format": "s"
      }
    },
    "color": {
      "field": "source_ip_with_total",
      "type": "nominal",
      "title": "Source IP (Total Packets)",
      "sort": {"field": "total_packets.value", "order": "descending"},
      "legend": {
        "orient": "right",
        "labelLimit": 300
      }
    },
    "tooltip": [
      {"field": "time_data.key", "type": "temporal", "title": "Time"},
      {"field": "key", "title": "Source IP"},
      {"field": "time_data.packet_count.value", "title": "Current Packets"},
      {"field": "formatted_packets", "title": "Total Packets"}
    ]
  },
  "config": {
    "legend": {
      "labelFontSize": 12,
      "padding": 10
    },
    "kibana": {
      "hideWarnings": true
    }
  }
}
"}}

