      "legend": {
        "orient": "right",
        "labelLimit": 300
      }
    },
    "tooltip": [
      {"field": "time_data.key", "type": "temporal", "title": "Time"},
      {"field": "key.0", "title": "Source IP"},
      {"field": "key.1", "title": "Destination IP"},
      {"field": "time_data.packet_count.value", "title": "Current Packets"},
      {"field": "formatted_packets", "title": "Total Packets"}
    ]
  },
  "config": {
    "legend": {
      "labelFontSize": 12,
      "padding": 10
    },
    "kibana": {
      "hideWarnings": true
    }
  }
}
