{"title":"Top HTTP Host Header","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "data": {
    "url": {
       "%context%": "true",
       "%timefield%": "aws.networkfirewall.event.timestamp",
      "index": "flint_*networkfirewall*__mview",
      "body": {
        "size": 0,
        "aggs": {
          "hostnames": {
            "terms": {
              "field": "aws.networkfirewall.event.http.hostname",
              "size": 10,
              "order": {
                "total_events": "desc"
              }
            },
            "aggs": {
              "actions": {
                "terms": {
                  "field": "aws.networkfirewall.event.alert.action",
                  "size": 5
                },
                "aggs": {
                  "total_events": {
                    "value_count": {
                      "field": "aws.networkfirewall.event.http.hostname"
                    }
                  },
                  "time_series": {
                    "date_histogram": {
                      "field": "aws.networkfirewall.event.timestamp",
                      "fixed_interval": "24h"
                    },
                    "aggs": {
                      "event_count": {
                        "value_count": {
                          "field": "aws.networkfirewall.event.http.hostname"
                        }
                      }
                    }
                  }
                }
              },
              "total_events": {
                "value_count": {
                  "field": "aws.networkfirewall.event.http.hostname"
                }
              }
            }
          }
        }
      }
    },
    "format": {"property": "aggregations.hostnames.buckets"}
  },
  "transform": [
    {
      "flatten": ["actions.buckets"],
      "as": ["action_data"]
    },
    {
      "flatten": ["action_data.time_series.buckets"],
      "as": ["time_data"]
    },
    {
      "filter": "datum.action_data.key === 'allowed'"
    },
    {
      "calculate": "datum.total_events.value >= 1e9 ? format(datum.total_events.value/1e9, '.2f') + ' B' : datum.total_events.value >= 1e6 ? format(datum.total_events.value/1e6, '.2f') + ' M' : datum.total_events.value >= 1e3 ? format(datum.total_events.value/1e3, '.2f') + ' K' : datum.total_events.value",
      "as": "formatted_events"
    },
    {
      "calculate": "datum.key + ' (' + datum.formatted_events + ')'",
      "as": "hostname_action_with_total"
    }
  ],
  "mark": {
    "type": "area",
    "point": {
      "filled": false,
      "fill": "white"
    }
  },
  "encoding": {
    "x": {
      "field": "time_data.key",
      "type": "temporal",
      "timeUnit": "yearmonthdatehoursminutes",
      "axis": {
        "title": "Time",
        "format": "%Y-%m-%d",
        "labelAngle": -45
      }
    },
    "y": {
      "field": "time_data.event_count.value",
      "type": "quantitative",
      "title": "Number of Blocked Events",
      "axis": {
        "format": "s"
      }
    },
